                    );

                    let champInfoCard = document.createElement('div');
                    champInfoCard.classList.add('champ_info__card__item');
                    champAlreadyClicked.push(name);

                    setTimeout(() => {

                        rankTier === undefined ? rankTier = 'UNRANKED' : rankTier;
